import React, { Component } from 'react';
import InputFullSalary from './InputFullSalary';
import InputReadOnly from './InputReadOnly';
import ProgressBarSalary from './ProgressBarSalary';

export default class Header extends Component {
  handleInputChange = (value) => {
    this.props.onChangeFilter(value);
  };

  render() {
    const {
      fullSalary,
      baseINSS,
      discountINSS,
      baseIRPF,
      discountIRPF,
      netSalary,
      pDiscountINSS,
      pDiscountIRPF,
      pNetSalary,
    } = this.props;

    return (
      <div>
        <InputFullSalary
          fullSalary={fullSalary}
          onChangeFilter={this.handleInputChange}
        />
        <InputReadOnly
          baseINSS={baseINSS}
          discountINSS={discountINSS}
          baseIRPF={baseIRPF}
          discountIRPF={discountIRPF}
          netSalary={netSalary}
        />
        <ProgressBarSalary
          pDiscountINSS={pDiscountINSS}
          pDiscountIRPF={pDiscountIRPF}
          pNetSalary={pNetSalary}
        />

        <footer>
          <div>
            Desenvolvido por <strong>Francisco Jr Soccol</strong>
          </div>
        </footer>
      </div>
    );
  }
}
