import React, { Component } from 'react';
import Countries from './components/country/Countries';
import Header from './components/header/Header';

export default class App extends Component {
  constructor() {
    super();

    this.state = {
      allCountries: [],
      filteredCountries: [],
      filterPopulation: 0,
      filter: '',
    };
  }

  async componentDidMount() {
    const res = await fetch('https://restcountries.eu/rest/v2/all');
    const json = await res.json();

    const allCountries = json.map(({ name, numericCode, population, flag }) => {
      return {
        id: numericCode,
        name,
        filterName: name.toLowerCase(),
        population,
        flag,
      };
    });

    this.setState({
      allCountries,
      filteredCountries: Object.assign([], allCountries),
    });
  }

  handleChangeFilter = (newFilter) => {
    this.setState({
      filter: newFilter,
    });

    const filterToLowerCase = newFilter.toLowerCase();

    const filteredCountries = this.state.allCountries.filter((country) => {
      return country.filterName.includes(filterToLowerCase);
    });

    const totalPopulation = filteredCountries.reduce((accumulator, current) => {
      return accumulator + current.totalPopulation;
    }, 0);

    this.setState({
      filteredCountries,
      filterPopulation: totalPopulation,
    });
  };

  render() {
    const { filteredCountries, filterPopulation, filter } = this.state;

    return (
      <div className="container">
        <h2>Countries</h2>
        <Header
          filter={filter}
          countryCount={filteredCountries.length}
          totalPopulation={filterPopulation}
          onChangeFilter={this.handleChangeFilter}
        />

        <Countries countries={filteredCountries} />
      </div>
    );
  }
}
