import React, { Component } from 'react';
import { calculateSalaryFrom } from './components/salary/Salary';
import Header from './components/header/Header';
import { formatNumber } from './helpers/formatHelpers';

export default class App extends Component {
  constructor() {
    super();

    this.state = {
      fullSalary: 1000,
      baseINSS: '',
      discountINSS: '',
      baseIRPF: '',
      discountIRPF: '',
      netSalary: '',
    };
  }

  async componentDidMount() {
    this.calcularValores(this.state.fullSalary);
  }

  calcularValores = (valor) => {
    const {
      baseINSS,
      discountINSS,
      baseIRPF,
      discountIRPF,
      netSalary,
    } = calculateSalaryFrom(valor);

    const pDiscountINSS = (discountINSS / valor) * 100;
    const pDiscountIRPF = (discountIRPF / valor) * 100;

    console.log(discountINSS, formatNumber(discountINSS));

    this.setState({
      fullSalary: valor,
      baseINSS: `R$ ${formatNumber(baseINSS)}`,
      discountINSS: `R$ ${formatNumber(discountINSS)} (${formatNumber(
        pDiscountINSS
      )} %)`,
      baseIRPF: `RF ${formatNumber(baseIRPF)}`,
      discountIRPF: `R$ ${formatNumber(discountIRPF)} (${pDiscountIRPF.toFixed(
        2
      )} %)`,
      netSalary: `R$ ${formatNumber(netSalary)}`,
    });
  };

  /*calculateTotalPopulation = (countries) => {
    return countries.reduce((accumulator, current) => {
      return accumulator + current.population;
    }, 0);
  };*/

  handleChangeFilter = (newValor) => {
    this.calcularValores(newValor);
    /*this.setState({
      filter: newFilter,
    });

    const filterToLowerCase = newFilter.toLowerCase();

    const filteredCountries = this.state.allCountries.filter((country) => {
      return country.filterName.includes(filterToLowerCase);
    });

    this.setState({
      filteredCountries,
      filterPopulation: this.calculateTotalPopulation(filteredCountries),
    });*/
  };

  render() {
    const {
      fullSalary,
      baseINSS,
      discountINSS,
      baseIRPF,
      discountIRPF,
      netSalary,
    } = this.state;

    return (
      <div className="container">
        <h2 style={style.title}>React Sal√°rio</h2>
        <Header
          fullSalary={fullSalary}
          onChangeFilter={this.handleChangeFilter}
          baseINSS={baseINSS}
          discountINSS={discountINSS}
          baseIRPF={baseIRPF}
          discountIRPF={discountIRPF}
          netSalary={netSalary}
        />
      </div>
    );
  }
}

const style = {
  title: {
    textAlign: 'center',
  },
};
